Using composer:  curl  $data = [
     region     =  1
     type       =  Temperature,
     dateFrom   =  now(),
     dateTo     =  now(),
     interval   =  '5m'
];

$curl = new \Curl\Curl();
$curl- post( https://dashboard.kukua.cc/api/sensordata/get ,
    $data
);
if ($curl- error) {
    return $curl- errorMessage;
}
return $curl- response;
